import React, { useState, useEffect } from 'react';
import axios from 'axios';
import ToDoLists from './ToDoLists';

const App = () => {
  const [inputList, setInputList] = useState("");
  const [Items, setItems] = useState([]);

  // Fetch items from the backend on component mount
  useEffect(() => {
    const fetchTodos = async () => {
      const result = await axios.get('http://localhost:5000/todos');
      setItems(result.data);
    };
    fetchTodos();
  }, []);

  const itemEvents = (event) => {
    setInputList(event.target.value);
  };

  const listOfItems = async () => {
    const result = await axios.post('http://localhost:5000/todos', { text: inputList });
    setItems((oldItems) => [...oldItems, result.data]);
    setInputList("");
  };

  const deleteItems = async (id) => {
    await axios.delete(`http://localhost:5000/todos/${id}`);
    setItems((oldItems) => oldItems.filter((item) => item._id !== id));
  };

  return (
    <>
      <div className="main_div">
        <div className="center_div">
          <br />
          <h1>ToDo List</h1>
          <br />
          <input 
            type="text" 
            placeholder="Add an Item"
            value={inputList}
            onChange={itemEvents} 
          />
          <button onClick={listOfItems}> + </button>
          <div className="todo_style">
            <ol>
              {Items.map((item) => {
                return (
                  <ToDoLists 
                    key={item._id}
                    id={item._id}
                    text={item.text}
                    onSelect={deleteItems} 
                  />
                );
              })}
            </ol>
          </div>
        </div>
      </div>
    </>
  );
}

export default App;
